import React, { Component } from "react";

class Counter extends Component {
  state = {
    count: 0
  };

  styles = {
    fontSize: 50,
    fontWeight: "bold"
  };
  render() {
    return (
      <div>
        <span className={this.updateClass()} style={{ fontSize: 15 }}>
          {this.formatCount()}
        </span>
        <button className="bbtn btn-seconday btn-sm m-2">Increment</button>
      </div>
    );
  }

  formatCount() {
    const { count } = this.state;
    return count === 0 ? "No Item" : count + " Item";
  }

  updateClass() {
    let classes = "badge badge-";
    classes += this.state.count === 0 ? "warning" : "primary";
    return classes;
  }
}

export default Counter;
